	public String getActivationToken(String mail) {
		Date date = new Date();
		String concatention = mail + new Timestamp(date.getTime());
		StringBuffer hexString = new StringBuffer();
		try {
			MessageDigest digest = MessageDigest.getInstance("SHA-256");
			byte[] hash = digest.digest(concatention.getBytes("UTF-8"));
			for (int i = 0; i < hash.length; i++) {
				String hex = Integer.toHexString(0xff & hash[i]);
				if (hex.length() == 1)
					hexString.append('0');
				hexString.append(hex);
			}
			this.token = hexString.toString();
		} catch (Exception ex) {
			this.token = null;
			throw new RuntimeException(ex);
		}
		return this.token;
	}

